mapper.select:
select 
tb_cart.id,count
from tb_cart
where userid = #{userid}
and goodsid = #{goodsid}

mapper.update:

update tb_cart
set addtime = #{addtime,jdbcType=TIMESTAMP},
  goodsid = #{goodsid,jdbcType=INTEGER},
  userid = #{userid,jdbcType=INTEGER},
  count = #{count,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}
