mapper.select:
    select 
    goods.id as id, goods.addtime, goods.goodsname, goods.classid, goods.brandid, goods.sellprice, goods.originalprice, goods.Inventory, goods.salevolume, goods.clicks, goods.state, goods.path, goods.volume, goods.weight, goods.goodsnumber, goods.recommend ,goods.details, brand.brandname as brandname, classes.classname as classname
    from tb_goods goods
    left join tb_brand brand on brand.id=goods.brandid
    left join tb_classes classes on classes.id=goods.classid
    where 1=1 

		and goods.goodsname like '%${goodsname}%'


		and goods.classid in 

         #{item}



		and goods.brandid =  #{brand_ids}


		and goods.sellprice >=  #{priceBegin}


		and goods.sellprice <=  #{priceEnd}


	order by goods.${orderBy} ${orderType}
